mapper.select:
select t1.id,t1.device_name,t2.model_name as deviceModel,t1.identify_en, t1.row_creater,t1.device_position, t1.row_create_time,t1.row_modifier,t1.row_modifier_time,t1.row_state from T_DEVICE as t1 inner join T_DEVICE_MODEL as t2 on t1.device_model=t2.id where t1.row_state>=0
and t1.row_state =#{params.state}
and t2.id = #{params.deviceModel}
and ( t1.device_name like concat('%',#{params.query},'%') or t1.device_position like concat('%',#{params.query},'%') or t2.model_name like concat('%',#{params.query},'%') )
order by t1.row_modifier_time desc limit #{limit},#{length};

select count(*) from T_DEVICE as t1 inner join T_DEVICE_MODEL as t2 on t1.device_model=t2.id where t1.row_state>=0
and t1.row_state =#{state}
and t2.id = #{deviceModel}
and ( t1.device_name like concat('%',#{query},'%') or t1.device_position like concat('%',#{query},'%') or t2.model_name like concat('%',#{query},'%') )
